// src/pages/Upload.js
import React, { useState } from "react";
import axios from "axios";

const Upload = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!file) {
      setMessage("Please select a PDF file before uploading.");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("file", file);

    try {
      const res = await axios.post("http://localhost:8080/api/notes", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setMessage("‚úÖ Uploaded successfully!");
      console.log(res.data);
    } catch (err) {
      console.error(err);
      setMessage("‚ùå Upload failed!");
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "40px auto", textAlign: "center" }}>
      <h2>üìÑ Upload a PDF Note</h2>

      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          style={{ padding: "8px" }}
        />

        <textarea
          placeholder="Content / Description"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="4"
          style={{ padding: "8px" }}
        />

        <input
          type="file"
          accept="application/pdf"
          onChange={(e) => setFile(e.target.files[0])}
          required
          style={{ padding: "8px" }}
        />

        <button type="submit" style={{ padding: "10px", cursor: "pointer", background: "#4CAF50", color: "#fff" }}>
          Upload
        </button>
      </form>

      {message && <p style={{ marginTop: "15px", fontWeight: "bold" }}>{message}</p>}
    </div>
  );
};

export default Upload;
