import React, { useState, useEffect } from "react";
import { getNotes, createNote } from "../api";
import "./Notes.css";

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  // Fetch all notes when page loads
  useEffect(() => {
    const fetchNotes = async () => {
      const data = await getNotes();
      setNotes(data);
    };
    fetchNotes();
  }, []);

  // Create new note
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !content) return alert("Please fill in both fields.");
    const newNote = await createNote({ title, content });
    setNotes([...notes, newNote]);
    setTitle("");
    setContent("");
  };

  // Delete note
  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:8080/api/notes/${id}`, { method: "DELETE" });
      setNotes(notes.filter((note) => note._id !== id));
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  return (
    <div className="notes-container">
      <h2>ðŸ“˜ My Notes</h2>

      <form className="note-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Note title..."
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Write your note here..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
        ></textarea>
        <button type="submit">Add Note</button>
      </form>

      <div className="notes-list">
        {notes.length === 0 ? (
          <p className="no-notes">No notes yet. Add your first one!</p>
        ) : (
          notes.map((note) => (
            <div key={note._id} className="note-card">
              <div className="note-header">
                <h3>{note.title}</h3>
                <button
                  className="delete-btn"
                  onClick={() => handleDelete(note._id)}
                >
                  Delete
                </button>
              </div>
              <p>{note.content}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Notes;
