import React, { useState } from "react";
import "./Upload.css";

function Upload() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [file, setFile] = useState(null);
  const [status, setStatus] = useState("");

  // ‚úÖ Auto-detect backend URL (local or deployed)
  const API_URL =
    process.env.NODE_ENV === "production"
      ? "https://your-backend-url.onrender.com" // üîÅ replace this with your deployed backend URL
      : "http://localhost:5000";

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setStatus("‚ö†Ô∏è Please select a file.");
      return;
    }

    const formData = new FormData();
    formData.append("subject", title); // 'title' acts as the subject
    formData.append("description", description);
    formData.append("file", file);

    try {
      const res = await fetch(`${API_URL}/api/notes/upload`, {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      if (data.success) {
        setStatus("‚úÖ File uploaded successfully!");
        setTitle("");
        setDescription("");
        setFile(null);
      } else {
        setStatus("‚ùå Upload failed. Try again.");
      }
    } catch (error) {
      console.error(error);
      setStatus("‚ùå Server error. Please try again later.");
    }
  };

  return (
    <div className="upload-container">
      <h2 className="upload-title">Upload Your Notes</h2>

      <form className="upload-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title / Subject (e.g. DSA)"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Short description..."
          rows="4"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>

        <input
          type="file"
          accept=".pdf,.doc,.ppt,.txt"
          onChange={(e) => setFile(e.target.files[0])}
          required
        />

        <button type="submit">Upload</button>
      </form>

      {status && <p className="upload-status">{status}</p>}
    </div>
  );
}

export default Upload;
